mapper.update:
update film set name = #{name}, cover = #{cover}, descr = #{descr}, year = #{year}, director = #{director},
actors = #{actors}, category_id = #{categoryId}, country = #{country}, language = #{language},
date = #{date}, duration = #{duration}, imdb = #{imdb}
where id = #{id}

mapper.select:
select film.*, category.name as categoryName from film left join category on film.category_id = category.id
and film.name like concat('%', #{name},'%') and film.category_id = #{categoryId}
order by film.id desc

select film.*, category.name as categoryName from film
left join category on film.category_id = category.id
where film.id = #{id}
